
public class NumberOfDigits {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=675480;
		int count = 0;
		 
		while(num!=0) {
			num=num/10;
			count = count+1;
			System.out.println(" the digits are:" +count);
			
			
		} 
		

		
		

	}

}
