
public class UnaryOperatorExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a= 5;
		int preIncNum = ++a;
		
		System.out.println("value of preIncNum is :"+ preIncNum);
		
		int b= 5;
		int postIncNum = b++;
		
		System.out.println("value of postIncNum is :"+ postIncNum);
		System.out.println("value of  b is :" + b);

	}

}
